        </i>
        <h5 class="card-title">{el.title}</h5>
          <span class="movie_info">{el.year}</span>
          <span class="movie_info float-right"><i class="fas fa-star"></i> 9 / 10</span>
      </div>
     </div>
    </div>
  ));

 }

export default Movies
