import './App.css';
import style from './css/style.css'
import Movienav from './components/movienav';
import Movies from './components/moviecard';
import bgacc from './bgacc.png';
import About from './components/movieabout';
import MovieFooter from './components/moviefooter';
import { BrowserRouter, Route } from "react-router-dom";
import Home from './components/moviehome';
import AboutMou from './components/aboutmouv';
import CardMovies from './components/cardmov';
import Favorit from './components/favorit';
import Contact from './components/contact';
import { useState } from 'react';
function App() {

  const[notheart , setNotheart] = useState(0);
  const addNotHeart = () => {
       setNotheart(notheart+1)
  }
  const [favorit , setFavorit] = useState([]);
  const addfavorit = (e) => {
    favorit.push(e)
    console.log(favorit)
  }

  const [searchMovie,setSearchMovie] = useState("");

  const handleSearchTerm = (e) => {
  let value = e.target.value
  setSearchMovie(value)
  }
  console.log(searchMovie);


  return (

    <div className="App">

       <div>
      <BrowserRouter>  


          <Route exact path="/"> <Home addNotHeart = {addNotHeart}  notheart={notheart} addfavorit = {addfavorit}  /> </Route>
          <Route   path="/about"> <AboutMou notheart={notheart} /></Route>
          <Route   path="/movies" ><CardMovies notheart={notheart} addNotHeart = {addNotHeart} addfavorit = {addfavorit}  /></Route>
          <Route   path="/favorit"> <Favorit favorit={favorit} notheart={notheart}/> </Route>
          <Route   path="/contact" > <Contact notheart={notheart} /></Route>


      </BrowserRouter>
    </div>


 </div>
  );
}

export default App;
