import {Card, Button} from 'react-bootstrap';
import React, {useState,useEffect}from 'react'

function Movies() {
    const [data, setData] = useState([])
  const getData = ()=>{

    fetch('movie.json').then(
          response => response.json()).then 
                   (movies => setData(movies));
  }

  useEffect(()=> {getData()
  },[])

  console.log(data)
  return(

      data.map(el=>

        <Card className='box grid'>



  <Card.Img className="img" variant="top" src={el.image} />
  <Card.Body>
    <Card.Title className="titre"><strong>{el.title}</strong></Card.Title>
    <Card.Text>
      Some quick example text to build on the card title and make up the bulk of
      the card's content.
    </Card.Text>
    <h5>{el.year}</h5>
    <p>{el.info}</p>
    <Button className="bouton" variant="primary">Watch Now</Button>
    <Button className="bouton" variant="primary">Watch Later</Button>
  </Card.Body>

    </Card>

      )

   )

 }

export default Movies
